import React, {useState} from 'react';
import $ from 'jquery'

//Bootstrap
import Button from 'react-bootstrap/Button'
import ListGroup from 'react-bootstrap/ListGroup'
import Form from 'react-bootstrap/Form'
import ListGroupItem from 'react-bootstrap/esm/ListGroupItem';

function Selector (props) {
    const [elements, setElements] = useState([])

    function addElement () {
        setElements(elements.concat([""]))
        props.setCount(props.count + 1)
    }

    function searchChangePlaceholder (newPlaceholder, index) {
        console.log("test")
        console.log(newPlaceholder)
        let newElements = [...elements];
        newElements[index] = newPlaceholder + ";false";
        setElements(newElements);
        setList(newElements);
        props.setRecs([]);
    }

    function changePlaceholder (newPlaceholder, index) {
        let newElements = [...elements];
        newElements[index] = newPlaceholder + ";false";
        setElements(newElements);
        setList(newElements);
        props.setRecs([]);
    }

    function removeElement (index) {
        let newElements = [...elements];
        newElements.splice(index,1);
        setElements(newElements);
        setList(newElements)
        props.setCount(props.count - 1);
        props.setRecs([]);
    }

    function changeValue (value, index) {
        let newElements = [...elements];
        newElements[index] = value + ";true";
        setElements(newElements);
        searchRecommendations(value);
    }

    function setList (newElements) {
        let newList = [...props.list]
        if (props.title === "Song") {
            newList[0] = newElements
            props.setList(newList)
        }
        else if (props.title === "Artist") {
            newList[1] = newElements
            props.setList(newList)
        }
        else if (props.title === "Genre") {
            newList[2] = newElements
            props.setList(newList)
        }
    }

    function setFocus (value, index) {
        let newElements = [...elements];
        newElements[index] = value + ";true";
        setElements(newElements);
        searchRecommendations(value);
    }

    function returnValue (element) {
        if (element != "")
        {
            return element.split(';')[0];
        }
        else return element;
    }

    function displayElements() {
        return (
            elements.map((element, index) => (
                <div>
                    <div class="input-group">
                        <input  type="text" 
                                class="form-control"
                                placeholder={props.title + " name"} 
                                value={returnValue(element)} 
                                aria-label="Recipient's username" 
                                aria-describedby="basic-addon2"
                                onChange={e => changeValue(e.target.value, index)}
                                onBlur={(e) => changePlaceholder(e.target.value, index)}
                                onFocus={(e) => setFocus(e.target.value, index)} />
                        <div class="input-group-append">
                            <button class="btn btn-outline-secondary" type="button" onClick={() => removeElement(index)}>X</button>
                        </div>
                    </div>
                    { !(element === "" || element.split(';')[1] === "false") &&
                    <ListGroup>
                        {searchRecs()}
                    </ListGroup>
                    }
                </div>
            ))
        )
    }

    function searchRecs() {
        if (props.recs !== [])
        {
            return (
                props.recs.map((element, index) => (
                    <ListGroupItem><Button >{element}</Button></ListGroupItem>  
                ))
            )
        }
    }

    function searchRecommendations(searchVal) {
        if (searchVal != "") {
            let resultArray = [];
            if (props.title === "Song")
            {
                const testParams = {
                    limit: 5
                }
                props.spotify.search(searchVal, ["track"], testParams)
                .then((response) => {
                    console.log(response.tracks.items)
                    response.tracks.items.map((item, index) => {
                        resultArray.push(item.name + " By: " + item.artists[0].name)
                    })
                    console.log(resultArray)
                    props.setRecs(resultArray)
                })
            }
            else if (props.title === "Artist")
            {
                const testParams = {
                    limit: 5
                }
                props.spotify.search(searchVal, ["artist"], testParams)
                .then((response) => {
                    response.artists.items.map((item, index) => {
                        resultArray.push(item.name)
                    })
                    props.setRecs(resultArray)
                })
            }
            else if (props.title === "Genre")
            {
                props.spotify.getAvailableGenreSeeds()
                .then((response) => {
                    let genres = response.genres.filter((genre) => genre.includes(searchVal))
                    if (genres.length > 5) {
                        props.setRecs(genres.slice(0, 5))
                    }
                    else props.setRecs(genres)
                })
            }
        }
    }

    return (
        <div>
            <label class="selectorLabel">{props.title}</label>
            {displayElements()}
            <br />
            {props.count < 5 && 
            <Button type="button" class="btn btn-default btn-circle btn-lg" onClick={() => addElement()}>+</Button>
            }
        </div>
    );
}

export default Selector;
