import React, {useState} from 'react';
import SpotifyWebApi from 'spotify-web-api-js';
import logo from './logo.svg';
import './App.css';

function App() {
  const spotifyApi = new SpotifyWebApi();
  const params = getHashParams()
  const token = params.access_token;
  if (token) {
    spotifyApi.setAccessToken(token);
  }

  const [loggedIn, setLoggedIn] = useState(token ? true : false);
  const [name, setName] = useState("Not Checked");
  const [albumArt, setAlbumArt] = useState("");
  const [topTracks, setTopTracks] = useState([]);

  function getHashParams() {
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    e = r.exec(q)
    while (e) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
       e = r.exec(q);
    }
    return hashParams;
  }

  function getNowPlaying() {
    spotifyApi.getMyCurrentPlaybackState()
      .then((response) => {
              setName(response.item.name) 
              setAlbumArt(response.item.album.images[0].url)
            }
        );
  }

  function getTopTracks() {
    const topOptions = {
      limit: 50
    }
    let newPlaylistId = "";

    const options = {
      public: false,
      name: "Generated Playlist"
    }
    spotifyApi.createPlaylist(params.current_user, options)
    .then(() => {
      let i = 0;
      while (newPlaylistId === "")
      {
        playlistOptions = {
          limit: 50,
          offset: i*50 
        }
        spotifyApi.getUserPlaylists(params.current_user, playlistOptions)
        .then(playlists => {
          console.log(playlists)
          playlists.items.forEach(item => {
            if (item.name === "Generated Playlist" && item.tracks.total === 0)
            {
              newPlaylistId = item.id;
            }
          })
        })
      }
    spotifyApi.getMyTopTracks(topOptions)
      .then((response) => {
        console.log(response.items[0].id)
        setTopTracks(response.items)
        let i = 0;
        for (i; i < 50; i += 5)
        {
          const tracks = {
            limit: 20,
            seed_tracks: [response.items[i].id, response.items[i + 1].id, response.items[i + 2].id, response.items[i + 3].id, response.items[i + 4].id]
          }
          spotifyApi.getRecommendations(tracks)
          .then((recommendations) => {
            console.log(recommendations)
            let trackNames = []

            recommendations.tracks.forEach((element) => {
              trackNames.push(element.id.toString())
            })

            spotifyApi.containsMySavedTracks(trackNames)
            .then(isSaved => {
              console.log(isSaved)
              let newTracks = 0;
              isSaved.forEach((element) => {
                if (!element && newTracks < 5)
                {
                  newTracks++;
                }
              })
            })
            .catch(error => {
              console.log(error)
            })
          })
        }
      })
    })

    console.log(topTracks[0])
  }

  function getSimilarTrack() {
    getTopTracks()
  }

  function createPlaylist() {
    const options = {
      public: false,
      name: "Generated Playlist"
    }
    spotifyApi.createPlaylist(params.current_user, options)
  }
  return (
    <div className="App">
      <a href='http://localhost:8888' > Login to Spotify </a>
      <div>
        Now Playing: {name}
      </div>
      <div>
        <img src={albumArt} style={{ height: 150 }}/>
      </div>
      {loggedIn &&
        <button onClick={() => getNowPlaying()}>
          Check Now Playing
        </button>
      }
      {loggedIn &&
        <button onClick={() => getSimilarTrack()}>
          Similar Track
        </button>
      }
      {loggedIn &&
        <button onClick={() => createPlaylist()}>
          Create Playlist
        </button>
      }      
    </div>
  );
}

export default App;
