import React, {useState} from 'react';

//Bootstrap
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import ListGroup from 'react-bootstrap/ListGroup'

//Component
import Selector from './Selector.js'

function CustomPlaylistForm () {
    const [selectorCount, setSelectorCount] = useState(0);
    const [searchList, setSearchList] = useState([[],[],[]])
    const [playlistName, setPlaylistName] = useState("Playlist")
    const [playlistDescription, setPlaylistDescription] = useState("")
    const [numberOfSongs, setNumberOfSongs] = useState(0)
    const [isPublic, setIsPublic] = useState(false)
    const [isCollab, setIsCollab] = useState(false)

    function handleSubmit () {
        console.log("test");
        console.log(searchList);
        console.log(playlistName);
        console.log(playlistDescription);
        console.log(numberOfSongs);
        console.log(isPublic);
        console.log(isCollab);
    }

    function handleName (name) {
        setPlaylistName(name);
    }

    function handleDescription (description) {
        setPlaylistDescription(description);
    }

    function handleNumber (number) {
        setNumberOfSongs(parseInt(number))
    }

    function handleCollab (boolVal) {
        setIsCollab(boolVal);
    }

    function handlePublic (boolVal) {
        setIsPublic(boolVal)
    }

    return (
        <div>
            <Form id="customForm">
                <Form.Group class="formGroup" controlId="playlistName">
                    <Form.Label class="formLabel">Playlist Name</Form.Label>
                    <Form.Control type="text" placeholder="Playlist" onChange={(e) => handleName(e.target.value)}/>
                </Form.Group>

                <Form.Group class="formGroup" controlId="playlistName">
                    <h4>Select Up To 5 Of The Following</h4>
                    <ListGroup horizontal>
                        <ListGroup.Item>
                            <Selector   title="Song" 
                                        place="Song name" 
                                        count={selectorCount} 
                                        setCount={setSelectorCount}
                                        list={searchList}
                                        setList={setSearchList}/>
                        </ListGroup.Item>
                        <ListGroup.Item>
                            <Selector   title="Artist"
                                        place="Artist name" 
                                        count={selectorCount} 
                                        setCount={setSelectorCount}
                                        list={searchList}
                                        setList={setSearchList}/>
                        </ListGroup.Item>
                        <ListGroup.Item>
                            <Selector   title="Genre" 
                                        place="Genre name" 
                                        count={selectorCount} 
                                        setCount={setSelectorCount}
                                        list={searchList}
                                        setList={setSearchList}/>
                        </ListGroup.Item>
                    </ListGroup>
                </Form.Group>

                <Form.Group class="formGroup" controlId="playlistName">
                    <Form.Label class="formLabel">Playlist Description</Form.Label>
                    <Form.Control as="textarea" rows="3" placeholder="Description" onChange={(e) => handleDescription(e.target.value)}/>
                </Form.Group>

                <Form.Group class="formGroup" controlId="playlistName">
                    <Form.Label class="formLabel">Number Of Songs</Form.Label>
                    <Form.Control type="text" placeholder="1-100" onChange={(e) => handleNumber(e.targe.value)}/>
                </Form.Group>

                <Form.Group class="formGroup" controlId="playlistName">
                    <Form.Check label="Public Playlist" onChange={(e) => handlePublic(e.target.checked)}/>
                    <Form.Check label="Collaborative Playlist" onChange={(e) => handleCollab(e.target.checked)}/>
                </Form.Group>

                <Button variant="primary" type="button" onClick={() => handleSubmit()}>
                    Create Playlist
                </Button>
            </Form>
        </div>
    );
}

export default CustomPlaylistForm;
